  handleLogout() {
    this.setState({
      loggedInStatus: "NOT_LOGGED_IN",
      user: {}
    });
  }

  handleLogin(data) {
    this.setState({
      loggedInStatus: "LOGGED_IN",
      user: data.user
    });
  }

  render(){
    return(
      <Router>
        <div className = "App">
          <Nav/>
          <Switch>
            <Route path="/" exact component ={Registration}/>
            <Route path="/login" component ={Login}/>
            <Route path="/predict" component ={TickerList}/>
          </Switch>
        </div>
      </Router>

    );
  }

}
