import React, { Component } from "react";
import axios from 'axios';

export default class App extends Component {
    constructor(props){
        super(props);

        this.state ={
            predictions : []
        }
    }

    componentWillMount(){
        const headers = {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + this.props.token
          }

        const data = {
            "ticker": this.props.ticker
        }

        axios.post("http://localhost:8080/prediction",
        { headers: headers, data: data }).then(response => {
            this.setState({predictions: response.data})

        }).catch(error => {
            console.log(error);
        });
        //event.preventDefault();
    }

    render(){

        console.log(this.state.predictions)
        if (this.props.predicted == true){
            return (
                <div>
                    {this.state.predictions.map((item, index) => (
                        <Item key={index} item={item} />
                    ))}
                </div>
            )
        } else{
            return(
                <div>
                    <h1>PICK A PREDICTION FOOL</h1>
                </div>
            )
        }

    }

}
